BEGIN

declare count int default 1000;
declare current_id long;
declare customer_cur cursor for select id from mycrm.customer;

open customer_cur;

update_customer_loop : loop

	fetch customer_cur into current_id;

	update customer set customer_seq_num = count, customer_id = concat('C00', convert(count,char))
